import { TestBed } from '@angular/core/testing';

import { CarritoService, productoCarrito, factura } from './carrito.service';

fdescribe('CarritoService', () => {
  let service: CarritoService;
  let productoNuevo: productoCarrito;
  let productodos: productoCarrito;
  let productotres: productoCarrito;
  beforeEach(() => {
    TestBed.configureTestingModule({});
    service = TestBed.inject(CarritoService);
    productoNuevo = {
      Imagen:
        'data:image/jpeg;base64,',
      NombreProducto: 'relog',
      cantidad: 1,
      valor: 5000,
      iva: 500,
    };
    productodos = {
      Imagen:
        'data:image/jpeg;base64,',
      NombreProducto: 'relog',
      cantidad: 3,
      valor: 7000,
      iva: 700,
    };
    productotres = {
      Imagen:
        'data:image/jpeg;base64,',
      NombreProducto: 'celular',
      cantidad: 3,
      valor: 9000,
      iva: 900,
    };
    service.añadirAlCarrito(productoNuevo);
    service.añadirAlCarrito(productodos);
    service.añadirAlCarrito(productotres);
  });

  it('El servicio debe ser creado', () => {
    expect(service).toBeTruthy();
  });

  it('El servicio debe añadir un producto', () => {
    expect(2).toEqual(service.getCarrito().length);
  });

  it('El servicio debe añadir un las unidades al produto existente', () => {
    expect(4).toEqual(service.getCarrito()[0].cantidad);
  });

  it('El servicio debe aumentar el valor de la factura', () => {
    expect(21000).toEqual(service.getValorFactura());
  });

  it('El servicio debe aumentar el valor del producto', () => {
    expect(12000).toEqual(service.getCarrito()[0].valor);
  });
  it('el servicio debe calcular el iva por producto', () => {
    expect(1200).toEqual(service.getCarrito()[0].iva);
    expect(900).toEqual(service.getCarrito()[1].iva);
  });
  it('El servicio debe aumentar la cantidad de la factura', () => {
    expect(7).toEqual(service.getTotalFactura());
  });
  it('El servicio debe aumentar el iva de la factura', () => {
    expect(2100).toEqual(service.getIvaFactura());
  });
});
